	var resetTxtBox = function (msgObj){
		    var srchEntryTxtBox = Cmn.GetObj(SRCH_ENTRY_TXT_BOX_ID);
	    
		    if(srchEntryTxtBox){
			srchEntryTxtBox.value = "";
		    }
	};
	
	// -------------------------------------------------
        // Summary: Updates the value of the search entry text box.
