			var stat = McMaster.SesnMgr.GetStVal(McMaster.SesnMgr.StValDefs.ChooseSpecsStat.KyTxt());

        	var currSrchRsltId = McMaster.SesnMgr.GetStVal(McMaster.SesnMgr.StValDefs.DynamicPageSrchRsltId.KyTxt());
