			var top = McMasterCom.Nav.GetTopFrame();
			trkEvnt = new top.PerfTracker.Evnt(top.PerfTracker.EvntNms.SpecSrchLdrAjaxCnxnEnd, top.PerfTracker.PgCntxtNms.DynCntnt); 
