        McMaster.MsgMgr.RemSubscriber(McMaster.MsgMgrMsgs.MainContentCntnrResized, resizeContentContainers);
        McMaster.MsgMgr.RemSubscriber(McMaster.MsgMgrMsgs.ChooseSpecsStatChg, resizeContentContainers);
